import React, { Component } from 'react'
import AlbumPhoto from '.././Photos/AlbumPhoto'

class ViewAlbum extends Component {

    constructor(props) {
        super(props);
        this.state = {photos: []};
    }

    componentDidMount() {
        fetch('http://localhost/server/Photos/GetPhotos.php?username=' 
        + localStorage.getItem('username') + '&album=' + this.props.match.params.album )
          .then(data => data.json())
          .then((data) => { this.setState({ photos: data }) }); 
    }

    delPhoto = (id) => {
        console.log(id)
        fetch('http://localhost/server/Photos/DeletePhoto.php?username=' 
        + localStorage.getItem('username') + '&id=' + id ) 
          .then(data => data.json())
          .then((data) => { this.setState({ albums: data }) });
    }

    editPhoto = (id) => {
        console.log(id)
        //this.props.history.push('/EditAlbum/'+id)
        /*fetch('http://localhost/server/Albums/DeleteAlbum.php?username=' 
        + localStorage.getItem('username') + '&id=' + id ) 
          .then(data => data.json())
          .then((data) => { this.setState({ albums: data }) });*/
    }

    render() {
        return (
            <div>
                <AlbumPhoto photos={this.state.photos} delPhoto={this.delPhoto}
                editPhoto={this.editPhoto}></AlbumPhoto>
            </div>
        )
    }
}

export default ViewAlbum
