import axios from 'axios';

export const login = user => {
    return axios
    .post('http://localhost/server/login.php',{
        username: user.username,
        password: user.password
    })
    .then(res =>{
        console.log(res)
    })
    .catch(error =>{
        console.log(error)
    })
}
