    constructor(){
        super()
        this.state = {
            firstName: '',
            lastName: '',
            email: '',
            gender: '',
            username: '',
            password: '',
            confirmPassword: ''
        }

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChange = (e) =>{
