import React, { Component } from 'react'

export class DisplayUserProfile extends Component {

    constructor(){
        super()
        this.state={
            FirstName:'ss',
            LastName:'',
            Email:'',
            Gender:''
        }
    }

    componentDidMount(){
        fetch('http://localhost/server/User_profiles/get_all_user_profiles.php?username=' 
        + this.props.user )
          .then(data => data.json())
          .then((data) => { console.log(data) }); 
    }


    render() {
        return (
            <div>
                <h3>First Name:{' '+this.state.FirstName}</h3>
                <br />
                <h3>Last Name:{' '+this.state.LastName}</h3>
                <br />
                <h3>Email:{' '+this.state.Email}</h3>
                <br />
                <h3>Gender:{' '+this.state.Gender}</h3>
                <br />
                <hr />
            </div>
        );
    }
}

export default DisplayUserProfile
