import React, { Component } from 'react'
import axios from 'axios';

export class ProfilePictureUpload extends Component {

    constructor(props) {
        super(props);
        this.state ={
            selectedFile:null
        }
    }

    fileChangedHandler = event => {
        this.setState({ selectedFile: event.target.files[0] })
    }

    uploadHandler = () => {
        if (this.state.selectedFile.size > 0){
            const fd = new FormData();
            fd.append('image', this.state.selectedFile, this.state.selectedFile.name);
            var headers = {
                'Content-Type': 'multipart/form-datan',
            }
            axios.post('http://localhost/server/auth/ProfilePicUpload.php?username='+ 
            localStorage.getItem('username'), fd, {headers: headers} 
            ).then(res => {
                localStorage.setItem('profile_pic', res.path)
                this.props.history.push('/dashboard')
            });
        }
    }
    render() {
        return (
            <div>
                <div>
                <input type="file" 
                    onChange={this.fileChangedHandler}
                />
                </div>
                <button
                    type="submit"
                    className="btn btn-primary"
                    onClick={this.uploadHandler}
                >
                Upload
                </button>
            </div>
        )
    }
}

export default ProfilePictureUpload
