import React, { Component } from 'react'

export class EditProfile extends Component {
    constructor(){
        super()
        this.state = {
            firstName: localStorage.getItem('fname'),
            lastName: localStorage.getItem('lname'),
            email: localStorage.getItem('email'),
            gender: localStorage.getItem('gender'),
            username: localStorage.getItem('username')
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChange = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    onSubmit = (e) =>{
        e.preventDefault();
        console.log(this.state)
        fetch('http://localhost/server/User_profiles/update_profile.php?username='+
        localStorage.getItem('username'),{
            method: 'POST',
            body: JSON.stringify({
                firstName: this.state.firstName,
                lastName: this.state.lastName,
                email: this.state.email,
                gender: this.state.gender,
            })
        })
        .then(res => res.json())
        .then(res => {
            if(res.updated) {
                localStorage.setItem('email', this.state.email)
                localStorage.setItem('gender', this.state.gender)
                localStorage.setItem('fname', this.state.firstName)
                localStorage.setItem('lname', this.state.lastName)
            }
            else{
                console.log(res.loggedIn)
            }
        })
        .catch(error => {
            console.log(error)
        });
    }


    render(){
        return( 
            <div className="container">
                <div className="row">
                    <div className="col-md-6 mt-5 mx-auto">
                        <form noValidate onSubmit={this.onSubmit}>
                            <h1 className="h3 mb-3 font-weight-normal">Update Profile</h1>
                            <div className="form-group">
                                <label htmlFor="firstName">First Name:</label>
                                <input type="text"
                                    className="form-control"
                                    name="firstName"
                                    placeholder="Enter your first name"
                                    value={this.state.firstName}
                                    onChange={this.onChange}

                                />
                            </div>
                            <br />
                            <div className="form-group">
                                <label htmlFor="lastName">Last Name:</label>
                                <input type="text"
                                    className="form-control"
                                    name="lastName"
                                    placeholder="Enter your last name"
                                    value={this.state.lastName}
                                    onChange={this.onChange}
                                />
                            </div>
                            <br />
                            <div className="form-group">
                                <label htmlFor="email">Email:</label>
                                <input type="text"
                                    className="form-control"
                                    name="email"
                                    placeholder="Enter your email"
                                    value={this.state.email}
                                    onChange={this.onChange}
                                />
                            </div>
                            <br />
                            <div>
                                <label htmlFor="gender">Gender:</label>
                                <input className="with-gap"
                                    type="radio"
                                    value="male"
                                    name="gender"
                                    checked={this.state.gender === "male"}
                                    onChange={this.onChange}
                                />
                                <span>Male</span>
                                <input className="with-gap"
                                    type="radio"
                                    value="female"
                                    name="gender"
                                    checked={this.state.gender === "female"}
                                    onChange={this.onChange}
                                />
                                <span>Female</span>
                            </div>
                            <br />
                            <button
                                type="submit"
                                className="btn btn-lg btn-primary btn-block"
                            >
                            UPDATE
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default EditProfile
