import React, { Component } from 'react'

export class Dashboard extends Component {
    constructor(){
        super()
        this.state={
            user: ''
        }

        this.set_user = this.set_user.bind(this)
    }

    set_user = () =>{
        this.state.user=localStorage.getItem('username')
    }

    render() {
        const isLoggedIn = localStorage.getItem('logged_in');
        const profile_pic = localStorage.getItem('profile_pic_exists')
        return (
            <div>
                {isLoggedIn ? (
                     profile_pic ? (<img src={ require('../../PROFILE/' + this.state.user +'.jpg') } />) 
                     : (<img src={ require('../../PROFILE/def.jpg') } />)
                ) : (
                    this.props.history.push('/')
                )}
            </div>
        )
    }
}

export default Dashboard
