import React, { Component } from 'react'
import { Link } from 'react-router-dom';

export class AlbumItem extends Component {
    render() {
        const id = this.props.album.id;
        return (
            <div>
                <div className="container">
                <img src={this.props.album.cover} alt="Can not be displayed" style={{width: 200}}></img>
                <Link to={"/ViewAlbum/" + this.props.album.AlbumName} cover={this.props.album.cover}>{ this.props.album.AlbumName }</Link>
                <p>{ this.props.album.AlbumDescription }</p>
                <p>{ this.props.album.Date_Time }</p>
                <button onClick={this.props.delAlbum.bind(this, id)} style={btnstyle}>x</button>
                <button onClick={this.props.editAlbum.bind(this, id)}>Edit</button>
                <Link to={"/EditAlbum/" + this.props.album.AlbumName}></Link>
                </div>
            </div>
        )
    }
}

const btnstyle={
    background:'#ff0000',
    color: '#fff',
    padding :'5px 10px',
    borderRadius: '50%',
    cursor: 'pointer',
    float: 'right'

}

export default AlbumItem
