import React, { Component } from 'react'

export class CreateAlbum extends Component {

    constructor(){
        super()
        this.state={
            AlbumName: '',
            AlbumDescription: '',
            SelectedCover:null
        }

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

