import React, { Component } from 'react'
import axios from 'axios';

export class ProfilePictureUpload extends Component {

    state = { selectedFile: null }

    fileChangedHandler = event => {
        this.setState({ selectedFile: event.target.files[0] })
    }

    uploadHandler = () => {
        if (this.state.selectedFile.size > 0){
            const fd = new FormData();
            fd.append('ProfilePic', this.state.selectedFile, this.state.selectedFile.name)
            console.log(fd)
            fetch('http://localhost/server/auth/ProfilePicUpload.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'multipart/form-data',  
                },
                body: fd
            })
            .then(res => res.json())
            .then(res => {
                console.log(res.uploaded)
            })
            .catch(error=>{
                console.log(error)
            })
        }

    }

    render() {
        return (
            <div>
                <div>
                <input type="file" 
                    onChange={this.fileChangedHandler}
                />
                </div>
                <button
                    type="submit"
                    className="btn btn-primary"
                    onClick={this.uploadHandler}
                >
                Upload
                </button>
            </div>
        )
    }
}

export default ProfilePictureUpload
