import React, { Component } from 'react'
import axios from 'axios';

class AddPhotosToAlbum extends Component {

    constructor(){
        super()
        this.state={
            SelectedCover:null
        }
        this.onSubmit = this.onSubmit.bind(this);
    }


    fileChangedHandler = event => {
        this.setState({ SelectedCover: event.target.files[0] })
    }

    onSubmit = (e) =>{
        e.preventDefault();

        const fd = new FormData();
        fd.append('image', this.state.SelectedCover, this.state.SelectedCover.name);
        for (var pair of fd.entries()) {
            console.log(pair[0]+ ', ' + pair[1]); 
        }
        var headers = {
            'Content-Type': 'multipart/form-data',
        }
        axios.post('http://localhost/server/Albums/CreateAlbum.php?username='+ 
        localStorage.getItem('username'), fd, {headers: headers} 
        ).then(res => {
            //localStorage.setItem('profile_pic', res.data.path)
            console.log(res.data)
            //console.log(res.data.path)
            //this.props.history.push('/AddPhotosToAlbum?album='+this.state.AlbumName)
        });
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-6 mt-5 mx-auto">
                        <form noValidate onSubmit={this.onSubmit}>
                            <h1 className="h3 mb-3 font-weight-normal">Upload Photos</h1>
                            <div>
                                <input type="file" 
                                    onChange={this.fileChangedHandler}
                                />
                            </div>
                            <button
                                type="submit"
                                className="btn btn-lg btn-primary btn-block"
                            >
                            Upload
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default AddPhotosToAlbum;
