import React, { Component } from 'react';
import  {Image, Text, StyleSheet} from 'react';

export class Dashboard extends Component {
    constructor(){
        super()
        this.state={
            user: 'swap'
        }

        //this.set_user = this.set_user.bind(this)
    }

    /*set_user = () =>{
        this.setlocalStorage.getItem('username')
    }*/
    onError = () => {
        if (!this.state.errored) {
          this.setState({
            src: '../../PROFILE/def.jpg',
            errored: true,
          });
        }
      }

    render() {
        const isLoggedIn = localStorage.getItem('logged_in');
        return (
            <div>
                {isLoggedIn ? (
                     <Image source={{ uri: '../../PROFILE/'+this.state.user }} onError={this.onError} />
                ) : (
                    this.props.history.push('/')
                )}
            </div>
        )
    }
}

export default Dashboard
